mapper.update:
UPDATE KSA_BD_CURRENCY_RATE_BYDATE SET
	RATE		= #{rate}
WHERE ID 	= #{id}


	UPDATE KSA_BD_CURRENCY_RATE_BYPARTNER SET
		RATE		= #{rate}
	WHERE ID 	= #{id}

mapper.select:
SELECT r.ID, r.RATE, r.MONTH, r.CURRENCY_ID, d.CODE as CURRENCY_CODE, d.NAME as CURRENCY_NAME, d.EXTRA as CURRENCY_RATE FROM KSA_BD_CURRENCY_RATE_BYDATE r JOIN KSA_BD_DATA d ON r.CURRENCY_ID = d.ID JOIN ( SELECT CURRENCY_ID, MAX( MONTH ) as LATEST_MONTH FROM KSA_BD_CURRENCY_RATE_BYDATE WHERE MONTH <= #{date} GROUP BY CURRENCY_ID ) t ON r.CURRENCY_ID = t.CURRENCY_ID AND r.MONTH = t.LATEST_MONTH
WHERE r.CURRENCY_ID = #{currencyId}
ORDER BY d.RANK

SELECT r.ID, r.RATE, r.MONTH, r.CURRENCY_ID, d.CODE as CURRENCY_CODE, d.NAME as CURRENCY_NAME, d.EXTRA as CURRENCY_RATE FROM KSA_BD_CURRENCY_RATE_BYDATE r JOIN KSA_BD_DATA d ON r.CURRENCY_ID = d.ID WHERE r.MONTH >= #{startDate} AND r.MONTH < #{endDate}
AND r.CURRENCY_ID = #{currencyId}
ORDER BY r.MONTH

SELECT r.ID, r.RATE, r.CURRENCY_ID, d.CODE as CURRENCY_CODE, d.NAME as CURRENCY_NAME, d.EXTRA as CURRENCY_RATE, r.PARTNER_ID, c.CODE as PARTNER_CODE, c.NAME as PARTNER_NAME FROM KSA_BD_CURRENCY_RATE_BYPARTNER r JOIN KSA_BD_DATA d ON r.CURRENCY_ID = d.ID JOIN KSA_BD_PARTNER c ON r.PARTNER_ID = c.ID WHERE r.PARTNER_ID = #{partnerId}
AND r.CURRENCY_ID = #{currencyId}
ORDER BY d.RANK
