mapper.select:

WHERE 1=1
 AND a.DIRECTION = ${direction} 
 AND a.NATURE = ${nature} 

	AND a.ID IN (
		SELECT c.ACCOUNT_ID
			FROM KSA_FINANCE_CHARGE c 
			LEFT JOIN KSA_LOGISTICS_BOOKINGNOTE	 	bn ON bn.ID = c.BOOKINGNOTE_ID
			WHERE 
				${clause}
			
		)


	
		AND ${clause}
	

 ORDER BY ${_sort} ${_order} 
 ORDER BY a.CREATED_DATE DESC

SELECT COUNT( * )
FROM KSA_FINANCE_ACCOUNT a
WHERE 1=1
 AND a.DIRECTION = ${direction} 
 AND a.NATURE = ${nature} 

	AND a.ID IN (
		SELECT c.ACCOUNT_ID
			FROM KSA_FINANCE_CHARGE c 
			LEFT JOIN KSA_LOGISTICS_BOOKINGNOTE	 	bn ON bn.ID = c.BOOKINGNOTE_ID
			WHERE 
				${clause}
			
		)


	
		AND ${clause}
