mapper.select:
		 
		WHERE c.BOOKINGNOTE_ID = #{id}
			
				AND DIRECTION = ${direction}
			
			
				AND NATURE = ${nature}
			
        ORDER BY DIRECTION desc, c.RANK

SELECT COUNT( c.ID ) FROM KSA_FINANCE_CHARGE c LEFT JOIN KSA_BD_PARTNER p ON p.ID = c.TARGET_ID LEFT JOIN KSA_BD_DATA d ON d.ID = c.CURRENCY_ID LEFT JOIN KSA_SECURITY_USER u ON u.ID = c.CREATOR_ID LEFT JOIN KSA_LOGISTICS_BOOKINGNOTE bn ON bn.ID = c.BOOKINGNOTE_ID LEFT JOIN KSA_FINANCE_ACCOUNT a ON a.ID = c.ACCOUNT_ID
